themes.content:
:root {
  /* Colors - CV Theme */
  --color-bg: #f5f5f5;
  --color-fg: #333333;
  --color-muted: #666666;
  --color-accent: #6aaf50;
  --color-red: #d32f2f;
  --color-border: #dddddd;
  --color-box: #e8e8e8;
  --chip-bg: #e8e8e8;
  --chip-fg: #333333;

  /* Typography - Monospace */
  --font-mono: 'Courier New', monospace;
  --h1: 24px;
  --h2: 13px;
  --text: 13px;
  --mono: 13px;
  --tracking: 2px;

  /* Layout */
  --page-w: 794px;
  --page-pad: 32px;
  --radius: 4px;
  --gap: 16px;
  --line: 1px;
  --band: 1px;
}

/* Invoice Preview Styles */
.invoice-preview {
  max-width: var(--page-w);
  margin: 0 auto;
  padding: var(--page-pad);
  background: white;
  color: var(--color-fg);
  font-family: var(--font-mono);
  font-size: var(--text);
  line-height: 1.8;
}

.invoice-header {
  margin-bottom: 12px;
  text-align: center;
}

.invoice-title {
  font-size: var(--h1);
  font-weight: bold;
  letter-spacing: var(--tracking);
  text-transform: uppercase;
  margin: 0;
  color: var(--color-fg);
}

.invoice-meta {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: var(--h2);
  color: var(--color-fg);
  margin: 8px 0;
}

.invoice-meta strong {
  color: var(--color-red);
}

.section-title {
  color: var(--color-fg);
  font-size: var(--h2);
  margin: 0 0 6px 0;
  font-weight: bold;
  text-transform: uppercase;
  letter-spacing: 1px;
}

.info-box {
  padding: 12px;
  margin-bottom: 12px;
  font-size: var(--text);
  line-height: 1.6;
}

.info-box strong {
  color: var(--color-red);
}

.highlight-box {
  padding: 10px 12px;
  margin: 12px 0;
  border-left: 2px solid var(--color-accent);
  font-size: var(--text);
}

.header-separator {
  border-top: var(--band) solid var(--color-accent);
  margin: 8px 0;
}

.table-wrapper {
  padding: 12px;
  margin: 16px 0;
}

.invoice-table {
  width: 100%;
  border-collapse: collapse;
  margin: 0;
  font-size: 12px;
}

.invoice-table thead th {
  text-align: left;
  font-size: 11px;
  font-weight: bold;
  text-transform: uppercase;
  padding: 8px 6px;
  border-bottom: var(--band) solid var(--color-accent);
  background: transparent;
}

.invoice-table tbody tr {
  border-bottom: var(--line) solid var(--color-border);
}

.invoice-table tbody tr:last-child {
  border-bottom: none;
}

.invoice-table td {
  padding: 6px;
  vertical-align: top;
}

.invoice-table td.text-right {
  text-align: right;
}

.summary-section {
  padding: 12px;
  margin: 16px 0;
  font-size: var(--text);
}

.summary-row {
  display: flex;
  justify-content: space-between;
  padding: 6px 0;
  border-bottom: var(--line) solid var(--color-border);
}

.summary-row:has(+ .summary-row.total) {
  border-bottom: none;
}

.summary-row.total {
  font-size: 15px;
  font-weight: bold;
  border-top: var(--band) solid var(--color-accent);
  border-bottom: var(--band) solid var(--color-accent);
  margin-top: 6px;
  padding-top: 10px;
}

.footer-section {
  margin-top: 24px;
  font-size: 12px;
  color: var(--color-muted);
}

:root {
  /* Colors matching the Facture theme */
  --color-bg: #ffffff;
  --color-fg: #333333;
  --color-box-bg: #f0f0f0;
  --color-green: #6aaf50;
  --color-red: #d32f2f;
  --color-blue: #2980b9;
  --color-orange: #e67e22;
  --color-border: #333333;

  /* Typography - Monospace */
  --font-mono: 'Courier New', monospace;
  --h1: 18pt;
  --h2: 11pt;
  --text: 10pt;

  /* Layout */
  --page-w: 794px;
  --page-pad: 40px;
  --gap: 15px;
}

/* CV Preview Container */
.cv-preview {
  max-width: var(--page-w);
  margin: 0 auto;
  padding: var(--page-pad);
  background: white;
  color: var(--color-fg);
  font-family: var(--font-mono);
  font-size: var(--text);
  line-height: 1.5;
}

/* Header */
.cv-header {
  text-align: center;
  margin-bottom: 30px;
}

.double-separator {
  border-top: 3px double var(--color-green);
  margin: 10px 0;
}

.cv-title {
  font-size: var(--h1);
  font-weight: bold;
  margin: 15px 0 5px 0;
  color: var(--color-fg);
  letter-spacing: 1px;
}

.cv-subtitle {
  font-size: var(--h2);
  color: var(--color-fg);
  margin: 5px 0 15px 0;
  letter-spacing: 0.5px;
}

/* Sections */
.cv-section {
  margin-bottom: 20px;
  page-break-inside: avoid;
}

.section-title {
  font-size: var(--text);
  font-weight: bold;
  margin-bottom: 8px;
}

/* Prompt part in green */
.section-title {
  color: var(--color-green);
}

/* Command part in gray */
span.command {
  color: #666666 !important;
  font-weight: normal !important;
}

.section-title span.command {
  color: #666666 !important;
  font-weight: normal !important;
}

.content-box {
  background-color: var(--color-box-bg);
  padding: 15px;
  margin-bottom: 5px;
}

.section-content {
  white-space: pre-line;
  line-height: 1.6;
}

/* Contact */
.contact-line {
  margin-bottom: 2px;
  line-height: 1.4;
}

/* Experience */
.experience-item {
  margin-bottom: 15px;
}

.experience-item:last-child {
  margin-bottom: 0;
}

.experience-header {
  margin-bottom: 5px;
  line-height: 1.4;
}

.experience-period {
  color: var(--color-red);
  font-weight: bold;
}

.arrow {
  color: var(--color-fg);
}

.experience-description {
  margin-top: 5px;
  margin-left: 0;
  white-space: pre-line;
  line-height: 1.5;
}

.experience-achievements {
  margin-top: 5px;
  margin-left: 0;
  white-space: pre-line;
  line-height: 1.5;
}

/* Skills */
.skill-category {
  margin-bottom: 8px;
  line-height: 1.6;
}

.skill-items {
  font-weight: normal;
}

.skill-item {
  display: inline;
}

/* Education */
.education-tree {
  margin-left: 0;
  line-height: 1.6;
}

.education-item {
  margin-bottom: 3px;
}

.tree-branch {
  color: var(--color-fg);
  margin-right: 5px;
}

.education-year {
  color: var(--color-red);
  font-weight: bold;
}

/* Languages */
.language-item {
  margin-bottom: 5px;
  line-height: 1.5;
}

/* Footer */
.footer-content {
  text-align: center;
  line-height: 1.6;
}

.footer-content a {
  color: var(--color-blue);
  text-decoration: underline;
}

/* Print styles */
@media print {
  .cv-preview {
    padding: 20px;
    max-width: 100%;
  }

  .content-box {
    break-inside: avoid;
  }
}

@page {
  margin: 15mm;
  size: A4;
}
